Script.script:
-------------------------------------------------
--         Put your Lua functions here.        --
--                                             --
-- Note that you can also use external Scripts --
-------------------------------------------------
mpkg = mpkg or { }
mpkg.database = db:create("MudletPackageManager", {
	repositories = {
		name = "",
		url = "",
		description = "",
		_unique = {"name"},
		_violations = "REPLACE"
	},
	packages = {
		name = "",
		version = "",
		installed = "",
		url = "",
		description = "",
		author = "",
		repository = "",
		extension = "",
		_index = { "name" }
	},
	dependencies = {
		name = "",
		dependency = "",
		repository = "",
		_index = { "name" }
	}
})

mpkg.transaction = { }
if db:aggregate(mpkg.database.packages.name, "COUNT") == 0 then
	db:add(mpkg.database.packages, {name = "MudletPackageManager", version = "0.0.1", installed = "0.0.1", url = "http://schova.de/mudlet-repository/MudletPackageManager.dat", author = "keneanung",
			description = "The Mudlet Package Manager", repository = "schova", extension = "xml" } )
	db:add(mpkg.database.repositories, {name = "schova", url = "http://schova.de/mudlet-repository/index.php?op=json",
			description = "The Base repository." } )
end

-------------------------------------------------
--         Put your Lua functions here.        --
--                                             --
-- Note that you can also use external Scripts --
-------------------------------------------------
mpkg.localization = {

	["en-US"] = {

		help = {
			headers = {
				"Help of the Mudlet Package Manager.",
				"The following commands are available:"
			},

			entries = {

				{ 
					command     = "mpkg help",
					description = {"Displays this help text."}
				},
				{
					command     = "mpkg repo add \<name\> \<URL\> \<Description\>",
					description = {"Adds a new repository with name, URL annd description to the database of known repositories."}
				},
				{
					command     = "mpkg repo list",
					description = {"Lists all known repositories."}
				},
				{
					command     = "mpkg repo info \<pattern\>",
					description = {
						"Show the URL and description of the repositories matching the pattern.",
						"The pattern may contain '_' as single character wildcard and '%' for multiple characters."
					},
				},
				{
					command     = "mpkg repo remove \<pattern\>",
					description = {
						"Removes the matching repositories from the database.",
						"The pattern may contain '_' as single character wildcard and '%' for multiple characters."
					},
				},
				{
					command     = "mpkg update",
					description = {"Updates the list of available packages on all repositories."}
